            if (avatar.files) msg.channel.send(`$&amp;#123;msg.author&amp;#125;`, avatar).catch(err =&amp;gt; &amp;#123; console.log(err) &amp;#125;);
            break;
            // case &amp;#39;test&amp;#39;:
            //     const testStr2 = msg.content.split(&amp;#39; &amp;#39;);
